const link=document.querySelector(".open-modal-button"),popup=document.querySelector(".modal-contact"),close=popup.querySelector(".modal-close"),form=popup.querySelector("form"),nameField=popup.querySelector("[name=name]"),nameEmail=popup.querySelector("[name=email]");let isStorageSupport=!0,storage="";try{storage=localStorage.getItem("nameField")}catch(e){isStorageSupport=!1}link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),storage?(nameField.value=storage,nameEmail.focus()):nameField.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){nameField.value&&nameEmail.value?isStorageSupport&&(localStorage.setItem("nameField",nameField.value),localStorage.setItem("nameEmail",nameEmail.value)):(e.preventDefault(),popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&(e.preventDefault(),popup.classList.remove("modal-show"))});